package arrays;

public class Employee {

	private String name;
	private int no;
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getNo() {
		return no;
	}
	public void setNo(int no) {
		this.no = no;
	}
	public Employee() {
		
		// TODO Auto-generated constructor stub
	}
	public Employee(String name, int no) {
		super();
		this.name = name;
		this.no = no;
	}
	
	
}
